<?php	require('macros.php');		if (!isset($_GET['TID'])) {		die("[MORE_PARAM_NEEDED:".MORE_PARAM_NEEDED."]");	}		$dbTable = new SQLite3(DATABASE_ORDER);	if (!$dbTable) {		header("HTTP/1.1 ERR_COULD_NOT_CONECT_DB 'ERR_COULD_NOT_CONECT_DB'");	  	die(ERR_COULD_NOT_CONECT_DB);	}	$tableId = $_GET['TID'];		$sql=sprintf("select %s,%s,%s,%s,%s,%s,%s.%s,%s from %s,%s where %s.%s = %s.%s and %s.%s = %s",				 ORDER_DETAIL_TABLE_COLUM_DISH_ID ,ORDER_DETAIL_TABLE_COLUM_PRICE,				 ORDER_DETAIL_TABLE_COLUM_ORDER_ID,ORDER_DETAIL_TABLE_COLUM_QUANTITY,				 TABLE_ORDER_TABLE_COLUM_TABLE_ID,TABLE_ORDER_TABLE_COLUM_TIMESTAMP,				 ORDER_DETAIL_TABLE,ORDER_DETAIL_TABLE_COLUM_ID,				 ORDER_DETAIL_TABLE_COLUM_STATUS,/*select*/				 ORDER_DETAIL_TABLE,TABLE_ORDER_TABLE,				 ORDER_DETAIL_TABLE,ORDER_DETAIL_TABLE_COLUM_ORDER_ID,/*from*/				 TABLE_ORDER_TABLE,TABLE_ORDER_TABLE_COLUM_ID,				 TABLE_ORDER_TABLE,TABLE_ORDER_TABLE_COLUM_TABLE_ID,"$tableId");		 	$resultSet = $dbTable->query($sql);	if ($resultSet) {		$i = 0;		while($row = $resultSet->fetchArray()) {			$item = array('dish_id' => $row[0],			 			  'price' => $row[1],						  'order_id' => $row[2],						  'quantity' => $row[3],						  'status' => $row[7]);			$Table[$i] = $item;			$i++;		}		$jsonString = json_encode($Table);	} else {		die(ERR_DB_QUERY);	}	echo "$jsonString";	$dbTable->close();?>